import logo from './logo.svg';
import './App.css';
import Container from './components/Container';
import List from './components/List';
import Selector from './components/Selector';


class App extends React.Component {
  state = {
    selectedList: null,
    lists: [
      { id: 1, name: 'List 1', items: ['Item 1', 'Item 2', 'Item 3'] },
      { id: 2, name: 'List 2', items: ['Item 4', 'Item 5', 'Item 6'] },
      { id: 3, name: 'List 3', items: ['Item 7', 'Item 8', 'Item 9'] },
    ],
  };

  handleListChange = (listId) => {
    this.setState({ selectedList: listId });
  };

  render() {
    const { selectedList, lists } = this.state;

    const selectedListItems =
      selectedList !== null
        ? lists.find((list) => list.id === parseInt(selectedList)).items
        : null;

    return (
      <Container>
        <Selector lists={lists} handleListChange={this.handleListChange} />
        {selectedListItems !== null ? (
          <List items={selectedListItems} />
        ) : (
          <p>Please select a list to display.</p>
        )}
      </Container>
    );
  }
}

export default App;
